import React, { useEffect, useState } from 'react';
import { onAuthStateChanged } from 'firebase/auth';
import { auth } from './firebase-config.js'; // Importez l'instance d'authentification

const Home = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                setIsAuthenticated(true);
            } else {
                setIsAuthenticated(false);
            }
        });

        // Nettoyage de l'écouteur lors de la déconnexion du composant
        return () => unsubscribe();
    }, []);

    return (
        <div>
            <h1>Page des appartements</h1>
            {isAuthenticated && (
                <button onClick={() => navigate('/add-apartment')} className="add-apartment-button">
                    Ajouter un appartement
                </button>
            )}
            {/* Afficher les autres composants ou appartements ici */}
        </div>
    );
};

export default Home;
